import React, {useEffect, useState} from "react";
import CreateBoard from "./CreateBoard";
import './CardInfo.css'
import AddCard from "./AddCard";
import { ClearSharp } from "@mui/icons-material";


// once click the card, you can update card title, desc, and status
function CardInfo(props) {

    const [values,setValues] = useState({...props.card})
    const colors = ["lightblue","lightpink","orange"];
    const [selectedColor, setSelectedColor] = useState();

    const updateTitle = (value) => {
        setValues({ ...values, title: value });
      };
      const updateDesc = (value) => {
        setValues({ ...values, desc: value });
      };


    useEffect(() => {
        if (props.updateCard) props.updateCard(props.boardId, values.id, values);
      }, [values]);

    const addStatus = (value) => {
        const index = values.status?.findIndex((item) => item.text === value.text);
        if (index > -1) return;  
        setSelectedColor("");
        setValues({
          ...values,
          status: [...values.status, value],
        });
      };

      const removeLabel = (value) => {
        const tempStatus = values.status?.filter((item) => item.text !== value.text);

        setValues({
          ...values,
          status: tempStatus,
        });
      };


    return (

            <CreateBoard onClose={()=> props.onClose()}>
               <div className="cardinfo">
                <div className="container">
                    <div className="title">
                        <h3>Add New Task</h3>
                    </div> 
                    <div className="task_body">
                      <AddCard text={values.title} defaultValue={values.title}
                      buttonText="Set Title"
                      onSubmit={updateTitle}/>
                    </div>   

                    <div className="title">
                        <h3>Description</h3>
                    </div> 
                    <div className="task_body">
                      <AddCard text={values.desc}

                       defaultValue={values.desc}

                      onSubmit={updateDesc}/>
                    </div>  

                    <div className="title">
                        <h3>Status</h3>
                    </div> 

          <div className="status">
            {values.status?.map((item, index) => (
              <label
                key={index}
                style={{ backgroundColor: item.color, color: "white" }}
              >
                {item.text}
                <ClearSharp onClick={() => removeLabel(item)} />
              </label>
            ))}
          </div>
          <ul>
            {colors.map((item, index) => (
              <li
                key={index + item}
                style={{ backgroundColor: item }}
                className={selectedColor === item ? "li_active" : ""}
                onClick={() => setSelectedColor(item)}
              />
            ))}
          </ul>
          <AddCard text="Add Status"
            placeholder="Enter Status"
            onSubmit={(value) =>
              addStatus({ color: selectedColor, text: value })
            }
          />
        </div>

                </div> 

            </CreateBoard>


    )
}
export default CardInfo;
